package com.szy;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.ApplicationContext;import org.springframework.web.bind.annotation.RestController;@RestController@SpringBootApplicationpublic class ZstuJgDivisionApplication {	private static ApplicationContext context;	public static <T> T getBean(Class<T> clazz){		return context.getBean(clazz);	}	public static void main(String[] args) {		context = SpringApplication.run(ZstuJgDivisionApplication.class, args);	}}